mapper.select:
		SELECT *
        FROM
            (
                SELECT row_number()
                OVER (ORDER BY CreatedTime DESC)  As rownumber, [Marketing].*
                FROM [Marketing]
                WHERE IsDeleted='false'

                    AND name LIKE #{name}


                    AND type LIKE #{type}


                    AND status LIKE #{status}


                    AND manager LIKE #{manager}

            ) AS result
        WHERE rownumber >=#{startNumber}
        AND rownumber <=#{endNumber}

		SELECT count(1) from [Marketing]
        WHERE IsDeleted='false'

                    AND name LIKE #{name}


                    AND type LIKE #{type}


                    AND status LIKE #{status}


                    AND manager LIKE #{manager}
